// notification
import {
  initToken,
  requestPermission,
} from '@apis/notifications/getMessagingToken';

import {Suspense, useEffect, useState, useRef, lazy} from 'react';
import {BrowserRouter, Routes, Route} from 'react-router-dom';

import '@/App.scss';

import Loading from '@components/loading/Loading';
// route
import Intro from '@screens/Intro';
import Main from '@screens/Main';
import Globe from '@screens/Globe';
import Map from '@screens/Map';
// lazy loading으로 대체
// import MessageList from '@screens/MessageList';
import MessageCreate from '@screens/MessageCreate';
import MessageDetail from '@screens/MessageDetail';
import VR from '@screens/VR';
import NotFound from '@screens/NotFound';
import Individual from '@screens/Individual';

//recoil
import {useRecoilState, useRecoilValue} from 'recoil';
// router
import {
  main,
  intro,
  globe,
  map,
  vr,
  messageList,
  messageCreate,
  messageUpdate,
  messageDetail,
  notFound,
  settings,
  notifications,
} from '@apis/router';
import {bgmArr} from './assets/bgms';
import {bgmNoState, playingState} from './apis/Recoil';

function App() {
  initToken();

  // 배경음악
  const [isPlaying, setIsPlaying] = useRecoilState(playingState);
  const [bgmNo, setBgmNo] = useRecoilState(bgmNoState);

  // // 로컬에 데이터가 있으면 그 데이터로 전역 상태 업데이트
  // const bgmState = localStorage.getItem('bgmState');
  // if (bgmState === null) {
  //   localStorage.setItem(
  //     'bgmState',
  //     JSON.stringify({isPlaying: isPlaying, bgmNo: bgmNo}),
  //   );
  // } else {
  //   const parsedBgmState = JSON.parse(bgmState);
  //   setIsPlaying(parsedBgmState.isPlaying);
  //   setBgmNo(parsedBgmState.bgmNo);
  // }

  const audioRef = useRef<HTMLAudioElement>(null!);

  useEffect(() => {
    audioRef.current.volume = 1;
  }, []);

  useEffect(() => {
    if (isPlaying) {
      audioRef.current && audioRef.current.play();
    } else {
      audioRef.current && audioRef.current.pause();
    }
  }, [isPlaying]);

  const MessageList = lazy(() => import('@screens/MessageList'));

  return (
    <>
      {/* <InitMessage /> */}
      <BrowserRouter>
        {/* <RecoilRoot> */}
        <Suspense fallback={<Loading />}>
          <Routes>
            <Route path={intro()} element={<Intro />} />
            <Route path={main()} element={<Main />} />
            <Route path={globe()} element={<Globe />} />
            <Route path={map()} element={<Map />} />
            <Route path={vr()} element={<VR />} />
            <Route path={messageList()} element={<MessageList />} />
            <Route path={messageCreate()} element={<MessageCreate />} />
            <Route path={messageUpdate()} element={<MessageCreate />} />
            <Route path={messageDetail()} element={<MessageDetail />} />
            <Route path={notFound()} element={<NotFound />} />
            <Route path={settings()} element={<Individual />} />
            <Route path={notifications()} element={<Individual />} />
          </Routes>
        </Suspense>
        {/* </RecoilRoot> */}
      </BrowserRouter>
      <audio
        src={bgmArr[bgmNo].src}
        autoPlay={true}
        loop={true}
        ref={audioRef}
      />
    </>
  );
}
export default App;
