      </div> */}
      <div className="notifications-list">
        {newItems.length === 0 ? (
          <div className="notifications-empty">새로운 알림이 없습니다.</div>
        ) : (
          newItems.map((item, index) => {
            return (
              <MyNotification
                item={item}
                key={item.id}
                deleteEach={deleteEach}
                index={index}
                // onDragEnd={(e) => dragEndHandler(e)}
              />
            );
          })
        )}
      </div>
    </div>
  );
}

