import {ReactComponent as Play} from '@images/play.svg';
import {ReactComponent as Pause} from '@images/pause.svg';
import '@individual/SettingsModal.scss';
import {useRecoilState} from 'recoil';
import {playingState, bgmNoState} from '@/apis/Recoil';

interface BgmListItemProps {
  title: string;
  src: string;
  index: number;
}

function BgmListItem({title, src, index}: BgmListItemProps) {
  const [isPlaying, setIsPlaying] = useRecoilState(playingState);
  const [bgmNo, setBgmNo] = useRecoilState(bgmNoState);

  return (
    <div
      key={title}
      className={title === newBgm ? 'bgm-item bgm-selected' : 'bgm-item'}
      onClick={changeBgm(index)}>
      <span className="bgm-modal-title">{title}</span>

      <audio ref={audioRef} src={src} loop />
      {onPlay ? (
        <Pause className="bgm-play" fill="black" onClick={playBgm} />
      ) : (
        <Play className="bgm-play" fill="black" onClick={playBgm} />
      )}
    </div>
  );
}

export default BgmListItem;
