import {Dispatch, SetStateAction, useState} from 'react';
import {ReactComponent as Help} from '@images/help.svg';
import '@common/Common.scss';

interface BgmModalProps {
  open: boolean;
  onCancel: Dispatch<SetStateAction<boolean>>;
}

function BgmModal({open, onCancel}: BgmModalProps) {
  const [isOpened, setIsOpened] = useState(false);

  const closeHelp = () => setIsOpened(false);

  return (
    <>
      <div
        id="open-help"
        onClick={() => {
          setIsOpened(true);
        }}>
        <Help viewBox="0 0 45 50" width="40" height="40" fill="white" />
      </div>
      {isOpened ? (
        <div className="help-background" onClick={closeHelp}>
          <div className="container help-container"></div>
        </div>
      ) : null}
    </>
  );
}

export default BgmModal;
