import {useState, Dispatch, SetStateAction} from 'react';
import {ReactComponent as ArrowDropDown} from '@images/arrowDropDown.svg';
import '@common/SelectBox.scss';

interface SelectBoxProps {
  options: string[];
  setReportType: Dispatch<SetStateAction<number>>;
}

function SelectBox({setReportType, options}: SelectBoxProps) {
  // select 목록 표시 여부
  const [showSelect, setShowSelect] = useState(false);

  return (
    <>
      <div
        className="select-toggle"
        onClick={() => setShowSelect(prev => !prev)}>
        <span>신고 유형</span>
        <ArrowDropDown
          fill="#a07dff"
          viewBox="0 0 50 50"
          width="25"
          height="25"
        />
      </div>
      {showSelect ? (
        <ul className="select-box">
          {options.map((option: string, index) => (
            <li className="select-li" key={option}>
              <button
                className="select-option"
                onClick={() => console.log(index)}>
                {option}
              </button>
            </li>
          ))}
        </ul>
      ) : null}
    </>
  );
}

export default SelectBox;
